package co.edu.poli;

public class App {
	public static void main(String[] args) {

	}

	public static float valorAbsoluto(float numero) {
		if (numero < 0) {
			return -numero;
		}

		return numero;
	}

	public static boolean esPar(int numero) {
		return numero % 2 == 0;
	}

	public static TipoTriangulo tipoTriangulo(float ladoA, float ladoB, float ladoC) {
		if (ladoA == ladoB && ladoB == ladoC) {
			return TipoTriangulo.EQUILATERO;
		}

		if (ladoA == ladoB || ladoB == ladoC || ladoA == ladoC) {
			return TipoTriangulo.ISOSCELES;
		}

		return TipoTriangulo.ESCALENO;
	}

	public static int juegoDeDardos(float x, float y) {
		x = valorAbsoluto(x);
		y = valorAbsoluto(y);

		float max = x > y ? x : y;

		// Rojo
		if (max <= 1) {
			return 15;
		}

		// Naranja
		if (max <= 2) {
			return 9;
		}

		// Amarillo
		if (max <= 3) {
			return 5;
		}

		// Verde
		if (max <= 4) {
			return 2;
		}

		// Azul
		if (max <= 5) {
			return 1;
		}

		return 0;
	}

	public static int distanciaAlBorde(int x, int y, int a, int b) {
		int min = x;

		if (y < min) {
			min = y;
		}
		if (a - x < min) {
			min = a - x;
		}

		if (b - y < min) {
			min = b - y;
		}

		return min;
	}

	public static int encuentroEnLaUniversidad(int a, int b, int c) {
		int i = 0;

		do {
			i++;
		} while (i % a != 0 || i % b != 0 || i % c != 0);

		return i;
	}

	public static boolean esPrimo(int n) {
		if (n <= 1) {
			return false;
		}

		if (n == 2) {
			return true;
		}

		if (n % 2 == 0) {
			return false;
		}

		int limite = (int) Math.sqrt(n);

		for (int i = 3; i < limite; i += 2) {
			if (n % i == 0) {
				return false;
			}
		}

		return true;
	}

	public static int[] conjeturaDeGoldbach(int numero) {
		for (int i = 2; i < numero; i++) {
			if (!esPrimo(i)) {
				continue;
			}

			for (int j = 2; j < numero; j++) {
				if (!esPrimo(j)) {
					continue;
				}

				if (i + j == numero) {
					return new int[] { i, j };
				}
			}
		}

		return null;
	}
}

enum TipoTriangulo {
	EQUILATERO, ISOSCELES, ESCALENO
}
